create table cms_article
(
    id                  varchar(64)      not null comment '编号'
        primary key,
    category_id         varchar(64)      not null comment '栏目编号',
    title               varchar(255)     not null comment '标题',
    link                varchar(255)     null comment '文章链接',
    color               varchar(50)      null comment '标题颜色',
    image               varchar(255)     null comment '文章图片',
    keywords            varchar(255)     null comment '关键字',
    description         varchar(255)     null comment '描述、摘要',
    weight              int  default 0   null comment '权重，越大越靠前',
    weight_date         datetime         null comment '权重期限',
    hits                int  default 0   null comment '点击数',
    posid               varchar(10)      null comment '推荐位，多选',
    custom_content_view varchar(255)     null comment '自定义内容视图',
    view_config         text             null comment '视图配置',
    create_by           varchar(64)      null comment '创建者',
    create_date         datetime         null comment '创建时间',
    update_by           varchar(64)      null comment '更新者',
    update_date         datetime         null comment '更新时间',
    remarks             varchar(255)     null comment '备注信息',
    del_flag            char default '0' not null comment '删除标记'
)
    comment '文章表' charset = utf8;

create index cms_article_category_id
    on cms_article (category_id);

create index cms_article_create_by
    on cms_article (create_by);

create index cms_article_del_flag
    on cms_article (del_flag);

create index cms_article_keywords
    on cms_article (keywords);

create index cms_article_title
    on cms_article (title);

create index cms_article_update_date
    on cms_article (update_date);

create index cms_article_weight
    on cms_article (weight);

